import React from 'react';
import './Newsletter.css';
import { Container, Row, Col } from 'reactstrap';

const Newsletter = () => {
  return (
    <section className="newsletter">
      <Container>
        <Row className="justify-content-center text-center">
          <Col lg="8">
            <div className="newsletter__content glass-card">
              <h2>üöÄ Get Ready to Explore</h2>
              <p>Join our mailing list to receive exclusive travel insights, early access to deals, and inspiring destinations.</p>

              <div className="newsletter__input">
                <input type="email" placeholder="Your email address" />
                <button className="btn newsletter__btn">Subscribe</button>
              </div>

              <small>Your adventure begins here ‚Äî no spam, just pure inspiration üåç</small>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Newsletter;
