import React, { useState, useContext } from 'react'
import '../styles/Login.css'
import { Link, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Form, FormGroup, Button } from 'reactstrap';
import loginImg from '../assets/images/login.png' // Replace with your new image
import userIcon from '../assets/images/user.png'
import { AuthContext } from '../context/AuthContext';
import { BASE_URL } from './../utils/config';

const Login = () => {
  const [credentials, setCredentials] = useState({
    email: '',
    password: ''
  });

  const { dispatch } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleChange = e => {
    setCredentials(prev => ({ ...prev, [e.target.id]: e.target.value }))
  };

  const handleClick = async e => {
    e.preventDefault();
    dispatch({ type: 'LOGIN_START' });

    try {
      const res = await fetch(`${BASE_URL}/auth/login`, {
        method: 'POST',
        headers: {
          'content-type': 'application/json'
        },
        credentials: 'include',
        body: JSON.stringify(credentials),
      });

      const result = await res.json();
      if (!res.ok) alert(result.message);

      dispatch({ type: 'LOGIN_SUCCESS', payload: result.data });
      navigate('/');
    } catch (err) {
      dispatch({ type: 'LOGIN_FAILURE', payload: err.message });
    }
  };

  return (
    <section className="login__section">
      <Container>
        <Row className="align-items-center">
          <Col lg="6" className="d-none d-lg-block">
            <div className="login__illustration">
              <img src={loginImg} alt="Login illustration" className="img-fluid" />
              <div className="illustration__overlay">
                <h3>Welcome Back!</h3>
                <p>Discover amazing tours and book your next adventure with us.</p>
              </div>
            </div>
          </Col>

          <Col lg="6" md="12">
            <div className="login__card">
              <div className="login__header">
                <img src={userIcon} alt="User icon" className="user__icon" />
                <h2>Sign In</h2>
                <p className="welcome__text">Please enter your credentials</p>
              </div>

              <Form onSubmit={handleClick} className="login__form">
                <FormGroup className="input__group">
                  <label htmlFor="email">Email</label>
                  <input
                    type="email"
                    id="email"
                    placeholder="Enter your email"
                    value={credentials.email}
                    onChange={handleChange}
                    required
                  />
                  <i className="ri-mail-line input__icon"></i>
                </FormGroup>

                <FormGroup className="input__group">
                  <label htmlFor="password">Password</label>
                  <input
                    type="password"
                    id="password"
                    placeholder="Enter your password"
                    value={credentials.password}
                    onChange={handleChange}
                    required
                  />
                  <i className="ri-lock-line input__icon"></i>
                </FormGroup>

                <div className="d-flex justify-content-between align-items-center mb-4">
                  <div className="form__check">
                    <input type="checkbox" id="remember" />
                    <label htmlFor="remember">Remember me</label>
                  </div>
                  <Link to="/forgot-password" className="forgot__password">
                    Forgot password?
                  </Link>
                </div>

                <Button className="login__btn" type="submit">
                  Login <i className="ri-arrow-right-line"></i>
                </Button>

                <div className="divider">
                  <span>or</span>
                </div>

                <div className="social__login">
                  <Button className="social__btn google__btn">
                    <i className="ri-google-fill"></i> Continue with Google
                  </Button>
                  <Button className="social__btn facebook__btn">
                    <i className="ri-facebook-fill"></i> Continue with Facebook
                  </Button>
                </div>

                <p className="signup__link">
                  Don't have an account? <Link to="/register">Sign up</Link>
                </p>
              </Form>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Login;
