import React, { useContext, useState } from 'react'
import './Booking.css'
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from 'reactstrap'
import { useNavigate } from 'react-router-dom'
import { AuthContext } from '../../context/AuthContext'
import { BASE_URL } from '../../utils/config'

const Booking = ({ tour, avgRating }) => {
  const { price, reviews, title } = tour
  const navigate = useNavigate()
  const { user } = useContext(AuthContext)

  const [booking, setBooking] = useState({
    userId: user && user._id,
    userEmail: user && user.email,
    tourName: title,
    fullname: '',
    phone: '',
    guestSize: 1,
    bookAt: '',
  })

  const handleChange = e => {
    setBooking(prev => ({ ...prev, [e.target.id]: e.target.value }))
  }

  const serviceFee = 1000
  const totalAmount = Number(price) * Number(booking.guestSize) + serviceFee

  const handleClick = async e => {
    e.preventDefault()

    try {
      if (!user) {
        return alert('Please sign in to book a tour')
      }

      const res = await fetch(`${BASE_URL}/booking`, {
        method: 'POST',
        headers: { 'content-type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify(booking)
      })

      const result = await res.json()
      if (!res.ok) return alert(result.message)

      navigate("/thank-you")
    } catch (err) {
      alert(err.message)
    }
  }

  return (
    <div className='booking shadow-lg p-4 rounded-4'>
      <div className="booking__top d-flex align-items-center justify-content-between mb-4">
        <h3 className='text-primary fw-bold'>₹{price} <span className='text-muted fs-6'>/person</span></h3>
        <span className='tour__rating d-flex align-items-center gap-1 text-warning'>
          <i className="ri-star-fill"></i>
          {avgRating ? `${avgRating} (${reviews.length})` : 'No reviews'}
        </span>
      </div>

      <Form className='booking__form' onSubmit={handleClick}>
        <h5 className='mb-3'>Traveler Information</h5>

        <FormGroup>
          <input type="text" placeholder='Full Name' id='fullname' required onChange={handleChange} />
        </FormGroup>

        <FormGroup>
          <input type="number" placeholder='Phone Number' id='phone' required onChange={handleChange} />
        </FormGroup>

        {/* Responsive Date + Guest Row */}
        <FormGroup className='d-flex flex-column flex-md-row gap-3'>
          <input type="date" id='bookAt' required onChange={handleChange} />
          <input type="number" min="1" id='guestSize' placeholder='Guests' required onChange={handleChange} />
        </FormGroup>

        {/* Price Summary */}
        <ListGroup className='mt-4'>
          <ListGroupItem className='d-flex justify-content-between'>
            <span>Price per person</span>
            <span>₹{price}</span>
          </ListGroupItem>
          <ListGroupItem className='d-flex justify-content-between'>
            <span>Service Fee</span>
            <span>₹{serviceFee}</span>
          </ListGroupItem>
          <ListGroupItem className='d-flex justify-content-between fw-bold'>
            <span>Total</span>
            <span>₹{totalAmount}</span>
          </ListGroupItem>
        </ListGroup>

        <Button type="submit" className="btn primary__btn w-100 mt-4">Book Now</Button>
      </Form>
    </div>
  )
}

export default Booking
