import React from 'react';
import { Container, Row, Col } from 'reactstrap';
import CommonSection from '../shared/CommonSection';
import '../styles/About.css';
import aboutImg from '../assets/images/about-us.jpg';

const About = () => {
  return (
    <>
      <CommonSection title="About Us" />

      <section className="about">
        <Container>
          <Row className="align-items-center">
            <Col lg="6" md="6">
              <div className="about__img">
                
              </div>
            </Col>

            <Col lg="6" md="6">
              <div className="about__content">
                <h2>Discover the World With Us</h2>
                <p>
                  We are passionate about creating unforgettable journeys for curious travelers.
                  With years of experience in the travel industry, we tailor every trip to suit your unique desires.
                </p>
                <p>
                  Whether you're dreaming of exotic landscapes, cultural adventures, or relaxing escapes, our dedicated team is here to make it happen.
                  From booking to exploring, we ensure your experience is smooth, safe, and exciting.
                </p>
                <p>
                  Let’s explore the world — one destination at a time!
                </p>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </>
  );
};

export default About;
